var MetadataManager = new function(){
	this.getValue = function(key, callback){
		DatabaseAccess.query("meta", "find", { key: key }, {}).then(function(response){
			if(response.length==0){
				callback(undefined);
			}else{
				callback(response[0]);
			}
		});
	}
	
	this.hasKey = function(key, callback){
		DatabaseAccess.query("meta", "find", { key: key }, {}).then(function(response){
			if(response.length==0){
				callback(false);
			}else{
				callback(true);
			}
		});
	}
}