var BizAccess = require("prometheus-biz-accessor");

ServiceWrapper = function(service_id){
	console.log("!!!!!!!!!!!!!!!!!!!!! wrapping service", service_id)
	this.service_id = service_id;
}

ServiceWrapper.prototype = new function(){
	this.emit = function(){
		var event_name=null;
		var payload=null;
		var callback=null;
		event_name = arguments[0];
		if(arguments.length==2){
			callback = arguments[1];
		}
		if(arguments.length==3){
			payload = arguments[1];
			callback=arguments[2];
		}
		BizAccess.emit_service_event(this.service_id, event_name, payload, callback);
	}
}

module.exports = ServiceWrapper;