var LayerManager = require("prometheus-layer-manager");

ServiceWrapper = function(service_id){
	this.service_id = service_id;
}

ServiceWrapper.prototype = new function(){
	
	this.emit = function(){
		console.log("emitting. arguments: ", arguments);
		var event_name=null;
		var payload=null;
		var callback=null;
		event_name = arguments[0];
		if(arguments.length==2){
			callback = arguments[1];
		}
		if(arguments.length==3){
			payload = arguments[1];
			callback=arguments[2];
		}
		console.log("payload", payload);
		console.log("LayerManager", LayerManager);
		LayerManager.biz_interface.emit_service_event(this.service_id, event_name, payload, callback);
	}
}

module.exports = ServiceWrapper;